"""program to look up hexadecimal colours"""CODE_TO_COLOUR = {"#00ffff": "aqua", "#ffbf00": "amber", "#fbceb1": "apricot",                "#89cff0": "babyblue", "#3ffee135": "bananayellow", "#ff7f50": "coral",                "#fffdd0": "cream", "#350c878": "emerald", "#4b0082": "indigo"}COLOUR_TO_CODE = {v.lower(): k for k, v in CODE_TO_COLOUR.items()}def main():    colour_name = menu()    while colour_name != "":        get_colour_name = input("\nWhich colour would you like to loop up? ").strip().lower()        if get_colour_name == 'q':            break        if get_colour_name in COLOUR_TO_CODE:            print(f"{get_colour_name} is {COLOUR_TO_CODE[get_colour_name]}")        else:            print(f"{get_colour_name} is not in the database.")def menu():    colour_name = list(CODE_TO_COLOUR.keys())    for colour_code, colour_name in CODE_TO_COLOUR.items():        print(f"{colour_name}")    print("-------------------")    print("Please enter colour to look up hexadecimal code")    print("Press 'q' to quit")    return colour_namemain()